mapper.select:
SELECT

FROM 	fx_line_rate
WHERE 	RUN_TIME = #{runTime, jdbcType=INTEGER}
	AND	LR_VER = 0;

mapper.insert:
insert into fx_line_rate (RUN_TIME, LR_VER, LINE_RATE1, LINE_RATE2, LINE_RATE3, LINE_RATE4, LINE_RATE5, LINE_RATET)
values (#{runTime, jdbcType=INTEGER}, 0, #{lineRate1,jdbcType=INTEGER}, #{lineRate2,jdbcType=INTEGER}, #{lineRate3,jdbcType=INTEGER},
  #{lineRate4,jdbcType=INTEGER}, #{lineRate5,jdbcType=INTEGER}, #{lineRateT,jdbcType=INTEGER});

mapper.update:
update fx_line_rate
	SET LR_VER = (SELECT MAX(IFNULL(LR_VER, 0)) + 1 AS LR_VER FROM fx_line_rate)
WHERE	RUN_TIME = #{runTime, jdbcType=INTEGER}
	AND	LR_VER = 0;
