mapper.select:
SELECT 	COUNT(RUN_TIME)
FROM 	fx_goods FG
WHERE	DATE_FORMAT(DATE_ADD(CONCAT(FG.GOODS_DATE, ' ', FG.GOODS_TIME), INTERVAL #{type1} MINUTE), '%Y-%m-%d') = #{startDate}
	AND	RUN_TIME = #{type1}

SELECT @rownum := @rownum + 1 AS row_number,FROM_UNIXTIME(TIMESTAMP / 1000), S_VAL, H_VAL, L_VAL,E_VAL FROM fx_result_sec
cross join (select @rownum := 0) r
WHERE result_date = CURDATE() and SUBSTRING(result_time,1, 2) = #{type1}
